import*as dom from"../utils/dom/index.js";import privateProps from"../privateProps.js";import{swalClasses}from"../utils/classes.js";function setButtonsDisabled(instance,buttons,disabled){const domCache=privateProps.domCache.get(instance);buttons.forEach(button=>{domCache[button].disabled=disabled})}function setInputDisabled(input,disabled){const popup=dom.getPopup();if(!popup||!input){return}if(input.type==="radio"){const radios=popup.querySelectorAll(`[name="${swalClasses.radio}"]`);for(let i=0;i<radios.length;i++){radios[i].disabled=disabled}}else{input.disabled=disabled}}export function enableButtons(){setButtonsDisabled(this,["confirmButton","denyButton","cancelButton"],false)}export function disableButtons(){setButtonsDisabled(this,["confirmButton","denyButton","cancelButton"],true)}export function enableInput(){setInputDisabled(this.getInput(),false)}export function disableInput(){setInputDisabled(this.getInput(),true)}