import globalState from"../globalState.js";import{animateTimerProgressBar,stopTimerProgressBar}from"../utils/dom/domUtils.js";export const getTimerLeft=()=>{return globalState.timeout&&globalState.timeout.getTimerLeft()};export const stopTimer=()=>{if(globalState.timeout){stopTimerProgressBar();return globalState.timeout.stop()}};export const resumeTimer=()=>{if(globalState.timeout){const remaining=globalState.timeout.start();animateTimerProgressBar(remaining);return remaining}};export const toggleTimer=()=>{const timer=globalState.timeout;return timer&&(timer.running?stopTimer():resumeTimer())};export const increaseTimer=ms=>{if(globalState.timeout){const remaining=globalState.timeout.increase(ms);animateTimerProgressBar(remaining,true);return remaining}};export const isTimerRunning=()=>{return!!(globalState.timeout&&globalState.timeout.isRunning())};