import{getContainer}from"./dom/getters.js";export const setAriaHidden=()=>{const container=getContainer();const bodyChildren=Array.from(document.body.children);bodyChildren.forEach(el=>{if(el.contains(container)){return}if(el.hasAttribute("aria-hidden")){el.setAttribute("data-previous-aria-hidden",el.getAttribute("aria-hidden")||"")}el.setAttribute("aria-hidden","true")})};export const unsetAriaHidden=()=>{const bodyChildren=Array.from(document.body.children);bodyChildren.forEach(el=>{if(el.hasAttribute("data-previous-aria-hidden")){el.setAttribute("aria-hidden",el.getAttribute("data-previous-aria-hidden")||"");el.removeAttribute("data-previous-aria-hidden")}else{el.removeAttribute("aria-hidden")}})};