import{swalClasses}from"../../classes.js";import*as dom from"../../dom/index.js";export const renderImage=(instance,params)=>{const image=dom.getImage();if(!image){return}if(!params.imageUrl){dom.hide(image);return}dom.show(image,"");image.setAttribute("src",params.imageUrl);image.setAttribute("alt",params.imageAlt||"");dom.applyNumericalStyle(image,"width",params.imageWidth);dom.applyNumericalStyle(image,"height",params.imageHeight);image.className=swalClasses.image;dom.applyCustomClass(image,params,"image")};