import{swalClasses}from"../../classes.js";import{addDraggableListeners,removeDraggableListeners}from"../../draggable.js";import*as dom from"../../dom/index.js";export const renderPopup=(instance,params)=>{const container=dom.getContainer();const popup=dom.getPopup();if(!container||!popup){return}if(params.toast){dom.applyNumericalStyle(container,"width",params.width);popup.style.width="100%";const loader=dom.getLoader();if(loader){popup.insertBefore(loader,dom.getIcon())}}else{dom.applyNumericalStyle(popup,"width",params.width)}dom.applyNumericalStyle(popup,"padding",params.padding);if(params.color){popup.style.color=params.color}if(params.background){popup.style.background=params.background}dom.hide(dom.getValidationMessage());addClasses(popup,params);if(params.draggable&&!params.toast){dom.addClass(popup,swalClasses.draggable);addDraggableListeners(popup)}else{dom.removeClass(popup,swalClasses.draggable);removeDraggableListeners(popup)}};const addClasses=(popup,params)=>{const showClass=params.showClass||{};popup.className=`${swalClasses.popup} ${dom.isVisible(popup)?showClass.popup:""}`;if(params.toast){dom.addClass([document.documentElement,document.body],swalClasses["toast-shown"]);dom.addClass(popup,swalClasses.toast)}else{dom.addClass(popup,swalClasses.modal)}dom.applyCustomClass(popup,params,"popup");if(typeof params.customClass==="string"){dom.addClass(popup,params.customClass)}if(params.icon){dom.addClass(popup,swalClasses[`icon-${params.icon}`])}};