import{swalClasses}from"./classes.js";export const measureScrollbar=()=>{const scrollDiv=document.createElement("div");scrollDiv.className=swalClasses["scrollbar-measure"];document.body.appendChild(scrollDiv);const scrollbarWidth=scrollDiv.getBoundingClientRect().width-scrollDiv.clientWidth;document.body.removeChild(scrollDiv);return scrollbarWidth};let previousBodyPadding=null;export const replaceScrollbarWithPadding=initialBodyOverflow=>{if(previousBodyPadding!==null){return}if(document.body.scrollHeight>window.innerHeight||initialBodyOverflow==="scroll"){previousBodyPadding=parseInt(window.getComputedStyle(document.body).getPropertyValue("padding-right"));document.body.style.paddingRight=`${previousBodyPadding+measureScrollbar()}px`}};export const undoReplaceScrollbarWithPadding=()=>{if(previousBodyPadding!==null){document.body.style.paddingRight=`${previousBodyPadding}px`;previousBodyPadding=null}};